mapper.update:
update t_member
set member_first_start=#{isFirst}
where member_id=#{memberId}
and team_id=#{teamId}
and member_first_start=1;

update t_member
set member_first_start=#{isFirst}
where member_id=#{memberId}
and team_id=#{teamId}
and member_first_start=0;

mapper.select:
select member_id,member_name,member_first_start,team_id
from t_member
where member_id=#{memberId}
and team_id=#{teamId};

mapper.delete:
delete from t_member
where member_id=#{memberId}
and team_id=#{teamId};

mapper.update:
update t_member
set member_name = #{memberName} and member_first_start=#{memberFirstStart}
where member_id=#{memberId};
